INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (21, "Recurring episodes of vertigo.You have a spinning sensation that starts and stops spontaneously. Episodes of vertigo occur without warning and usually last 20 minutes to several hours, but not more than 24 hours. Severe vertigo can cause nausea and vomiting.", 2760);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (22, "Hearing loss.Hearing loss in Meniere's disease may come and go, particularly early on. Eventually, most people have some permanent hearing loss.", 2760);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (23, "Ringing in the ear (tinnitus).Tinnitus is the perception of a ringing, buzzing, roaring, whistling or hissing sound in your ear.", 2760);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (24, "Feeling of fullness in the ear.People with Meniere's disease often feel pressure in the affected ears (aural fullness) or on the side of their heads.", 2760);
