INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (51, "Head lice.These lice develop on your scalp. They're easiest to see at the nape of your neck and over your ears.", 2857);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (52, "Body lice.These lice live in clothing and on bedding and move onto your skin to feed. Body lice most often affect people who aren't able to bathe or launder clothing regularly, such as homeless or transient individuals.", 2857);
