INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (359, "Diarrhea.Diarrhea is a common problem for people with Crohn's disease. Intensified intestinal cramping also can contribute to loose stools.", 900);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (360, "Fever and fatigue.Many people with Crohn's disease experience a low-grade fever, likely due to inflammation or infection. You may also feel tired or have low energy.", 900);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (361, "Abdominal pain and cramping.Inflammation and ulceration can affect the normal movement of contents through your digestive tract and may lead to pain and cramping. You may experience anything from slight discomfort to severe pain, including nausea and vomiting.", 900);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (362, "Blood in your stool.You might notice bright red blood in the toilet bowl or darker blood mixed with your stool. You can also have bleeding you don't see (occult blood).", 900);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (363, "Mouth sores.You may have ulcers in your mouth similar to canker sores.", 900);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (364, "Reduced appetite and weight loss.Abdominal pain and cramping and the inflammatory reaction in the wall of your bowel can affect both your appetite and your ability to digest and absorb food.", 900);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (365, "Perianal disease.You might have pain or drainage near or around the anus due to inflammation from a tunnel into the skin (fistula).", 900);
