INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (561, "Increased thirst and frequent urination.Excess sugar building up in your bloodstream causes fluid to be pulled from the tissues. This may leave you thirsty. As a result, you may drink &mdash; and urinate &mdash; more than usual.", 3447);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (562, "Increased hunger.Without enough insulin to move sugar into your cells, your muscles and organs become depleted of energy. This triggers intense hunger.", 3447);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (563, "Weight loss.Despite eating more than usual to relieve hunger, you may lose weight. Without the ability to metabolize glucose, the body uses alternative fuels stored in muscle and fat. Calories are lost as excess glucose is released in the urine.", 3447);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (564, "Fatigue.If your cells are deprived of sugar, you may become tired and irritable.", 3447);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (565, "Blurred vision.If your blood sugar is too high, fluid may be pulled from the lenses of your eyes. This may affect your ability to focus.", 3447);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (566, "Slow-healing sores or frequent infections.Type 2 diabetes affects your ability to heal and resist infections.", 3447);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (567, "Areas of darkened skin.Some people with type 2 diabetes have patches of dark, velvety skin in the folds and creases of their bodies &mdash; usually in the armpits and neck. This condition, called acanthosis nigricans, may be a sign of insulin resistance.", 3447);
