INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (887, "Poor muscle tone.A primary sign during infancy is poor muscle tone (hypotonia). Babies may rest with their elbows and knees loosely extended instead of fixed, and they may feel floppy or like rag dolls when they're held.", 1535);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (888, "Distinct facial features.Children may be born with almond-shaped eyes, a narrowing of the head at the temples, a turned-down mouth and a thin upper lip.", 1535);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (889, "Failure to thrive.Infants may have a poor sucking reflex due to decreased muscle tone. Because poor sucking makes feeding difficult, they tend to gain weight slowly.", 1535);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (890, "Lack of eye coordination (strabismus).The child's eyes may not move together &mdash; they may cross or wander to the side.", 1535);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (891, "Generally poor responsiveness.A baby may seem unusually tired, respond poorly to stimulation, have a hard time waking up or have a weak cry.", 1535);
