INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (1, "Delusions.These are false beliefs that are not based in reality. For example, you think that you're being harmed or harassed; certain gestures or comments are directed at you; you have exceptional ability or fame; another person is in love with you; or a major catastrophe is about to occur. Delusions occur in most people with schizophrenia.", 1225);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (2, "Hallucinations.These usually involve seeing or hearing things that don't exist. Yet for the person with schizophrenia, they have the full force and impact of a normal experience. Hallucinations can be in any of the senses, but hearing voices is the most common hallucination.", 1225);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (3, "Disorganized thinking (speech).Disorganized thinking is inferred from disorganized speech. Effective communication can be impaired, and answers to questions may be partially or completely unrelated. Rarely, speech may include putting together meaningless words that can't be understood, sometimes known as word salad.", 1225);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (4, "Extremely disorganized or abnormal motor behavior.This may show in a number of ways, from childlike silliness to unpredictable agitation. Behavior isn't focused on a goal, so it's hard to do tasks. Behavior can include resistance to instructions, inappropriate or bizarre posture, a complete lack of response, or useless and excessive movement.", 1225);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (5, "Negative symptoms.This refers to reduced or lack of ability to function normally. For example, the person may neglect personal hygiene or appear to lack emotion (doesn't make eye contact, doesn't change facial expressions or speaks in a monotone). Also, the person may have lose interest in everyday activities, socially withdraw or lack the ability to experience pleasure.", 1225);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (916, "Kidneys.Excess calcium in your blood means your kidneys have to work harder to filter it out. This can cause excessive thirst and frequent urination.", 3420);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (917, "Digestive system.Hypercalcemia can cause stomach upset, nausea, vomiting and constipation.", 3420);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (918, "Bones and muscles.In most cases, the excess calcium in your blood was leached from your bones, which weakens them. This can cause bone pain. Some people who have hypercalcemia also experience muscle weakness.", 3420);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (919, "Brain.Hypercalcemia can interfere with the way your brain works, resulting in confusion, lethargy and fatigue.", 3420);
