INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (1159, "Stress incontinence.Urine leaks when you exert pressure on your bladder by coughing, sneezing, laughing, exercising or lifting something heavy.", 806);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (1160, "Urge incontinence.You have a sudden, intense urge to urinate followed by an involuntary loss of urine. You may need to urinate often, including throughout the night. Urge incontinence may be caused by a minor condition, such as infection, or a more severe condition such as neurologic disorder or diabetes.", 806);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (1161, "Overflow incontinence.You experience frequent or constant dribbling of urine due to a bladder that doesn't empty completely.", 806);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (1162, "Functional incontinence.A physical or mental impairment keeps you from making it to the toilet in time. For example, if you have severe arthritis, you may not be able to unbutton your pants quickly enough.", 806);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (1163, "Mixed incontinence.You experience more than one type of urinary incontinence.", 806);
