INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (561, "Increased thirst and frequent urination.Excess sugar building up in your bloodstream causes fluid to be pulled from the tissues. This may leave you thirsty. As a result, you may drink &mdash; and urinate &mdash; more than usual.", 2909);
