INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (563, "Weight loss.Despite eating more than usual to relieve hunger, you may lose weight. Without the ability to metabolize glucose, the body uses alternative fuels stored in muscle and fat. Calories are lost as excess glucose is released in the urine.", 2909);
