INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (1580, "Localized.This is the most common type of granuloma annulare. The bump (lesion) borders have a circular or semicircular shape, with a diameter up to 2 inches (5 centimeters). It occurs most commonly on the hands, feet, wrists and ankles of young adults.", 3201);
