INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (741, "Poor regulation of body functions (autonomic nervous system).Blood pressure, pulse, sweating and the digestive process are regulated by a part of the nervous system that is often affected by Lewy body dementia. This can result in dizziness, falls and bowel issues such as constipation.", 1708);
