INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (34, "Flat, light brown spots on the skin (cafe au lait spots).These harmless spots are common in many people. Having more than six cafe au lait spots is a strong indication of NF1. They are usually present at birth or appear during the first years of life and then stabilize.", 2946);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (35, "Freckling in the armpits or groin area.Freckling usually appears by ages 3 to 5. Freckles are smaller than cafe au lait spots and tend to occur in clusters in skin folds.", 2946);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (36, "Tiny bumps on the iris of your eye (Lisch nodules).These harmless nodules can't easily be seen and don't affect your vision.", 2946);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (37, "Soft bumps on or under the skin (neurofibromas).These benign tumors usually develop in or under the skin, but can also grow inside of the body. Sometimes, a growth will involve multiple nerves (plexiform neurofibroma).", 2946);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (38, "Bone deformities.Abnormal bone growth and a deficiency in bone mineral density can cause bone deformities such as a curved spine (scoliosis) or bowed lower leg.", 2946);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (39, "Tumor on the optic nerve (optic glioma).These tumors usually appear by age 3, rarely in late childhood and adolescence, and almost never in adults.", 2946);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (40, "Learning disabilities.Impaired thinking skills are common in children with NF1, but are usually mild. Often there is a specific learning disability, such as problem with reading or mathematics. Attention-deficit/hyperactivity disorder (ADHD) is also common.", 2946);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (41, "Larger than average head size.Children with NF1 tend to have a larger than average head size due to increased brain volume.", 2946);
