INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (1742, "Widespread pain.The pain associated with fibromyalgia often is described as a constant dull ache that has lasted for at least three months. To be considered widespread, the pain must occur on both sides of your body and above and below your waist.", 2505);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (1743, "Fatigue.People with fibromyalgia often awaken tired, even though they report sleeping for long periods of time. Sleep is often disrupted by pain, and many patients with fibromyalgia have other sleep disorders, such as restless legs syndrome and sleep apnea.", 2505);
