INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (147, "Skin abnormalities.Some people with tuberous sclerosis have patches of light-colored skin, or they may develop small, harmless areas of thickened skin, or growths under or around the nails. Facial lesions that resemble acne also are common and can be treated.", 1161);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (148, "Seizures.Lesions in the brain may be associated with seizures, which can be the first symptom of tuberous sclerosis. In small children, a common type of seizure called infantile spasm shows up as repetitive spasms of the head and legs.", 1161);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (149, "Developmental delays.Tuberous sclerosis can be associated with intellectual disability, learning disabilities or developmental delays.", 1161);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (150, "Behavior problems.Common behavior problems may include hyperactivity, raging outbursts, aggression, repetitive behaviors, or social and emotional withdrawal.", 1161);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (151, "Communication and social interaction problems.Some children with tuberous sclerosis have trouble with communication and social interaction. And some children may have autism spectrum disorder.", 1161);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (152, "Kidney problems.Most people with tuberous sclerosis develop lesions on their kidneys, and they may develop more lesions as they age, sometimes damaging kidney function.", 1161);
