INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (154, "Lung problems.Lesions that develop in the lungs (pulmonary leiomyomas) may cause coughing or shortness of breath, especially with physical activity or exercise.", 1161);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (155, "Eye abnormalities.Lesions can appear as white patches on the light-sensitive tissue at the back of the eye (retina). These do not always interfere with vision.", 1161);
