INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (851, "History of other skin cancers.Merkel cell carcinoma is associated with the development of other skin cancers, such as basal cell or squamous cell carcinoma.", 1216);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (852, "Older age.Your risk of Merkel cell carcinoma increases as you age. This cancer is most common in people older than age 50, though it can occur at any age.", 1216);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (853, "Light skin color.Merkel cell carcinoma usually arises in people who have light-colored skin. Whites are much more likely to be affected by this skin cancer than are blacks.", 1216);
