INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (849, "Excessive exposure to natural or artificial sunlight.Being exposed to ultraviolet light, such as the light that comes from the sun or from tanning beds, increases your risk of Merkel cell carcinoma. The majority of Merkel cell carcinomas appear on skin surfaces frequently exposed to sun.", 2436);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (850, "A weakened immune system.People with weakened immune systems &mdash; including those with HIV infection, those taking drugs that suppress the immune response or those with chronic leukemias &mdash; are more likely to develop Merkel cell carcinoma.", 2436);
