INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (2060, "Sex.In general, women are more likely to have Sweet's syndrome than are men.", 1482);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (2061, "Age.Though older adults and even infants can develop Sweet's syndrome, the condition mainly affects people between the ages of 30 and 60.", 1482);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (2062, "Cancer.Sweet's syndrome is sometimes associated with cancer, most often leukemia. Sometimes, Sweet's syndrome may be associated with a solid tumor, such as breast or colon cancer.", 1482);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (2063, "Other health problems.Sweet's syndrome may follow an upper respiratory infection, and many people report having flu-like symptoms before the rash appears. Sweet's syndrome can also be associated with inflammatory bowel disease.", 1482);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (2064, "Pregnancy.Some women develop Sweet's syndrome during pregnancy.", 1482);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (2065, "Drug sensitivity.Sweet's syndrome may result from a sensitivity to medication. Drugs linked to Sweet's syndrome include azathioprine (Azasan, Imuran), granulocyte colony stimulating factor, certain antibiotics and some nonsteroidal anti-inflammatory drugs.", 1482);
