INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (34, "Flat, light brown spots on the skin (cafe au lait spots).These harmless spots are common in many people. Having more than six cafe au lait spots is a strong indication of NF1. They are usually present at birth or appear during the first years of life and then stabilize.", 2957);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (35, "Freckling in the armpits or groin area.Freckling usually appears by ages 3 to 5. Freckles are smaller than cafe au lait spots and tend to occur in clusters in skin folds.", 2957);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (36, "Tiny bumps on the iris of your eye (Lisch nodules).These harmless nodules can't easily be seen and don't affect your vision.", 2957);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (37, "Soft bumps on or under the skin (neurofibromas).These benign tumors usually develop in or under the skin, but can also grow inside of the body. Sometimes, a growth will involve multiple nerves (plexiform neurofibroma).", 2957);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (38, "Bone deformities.Abnormal bone growth and a deficiency in bone mineral density can cause bone deformities such as a curved spine (scoliosis) or bowed lower leg.", 2957);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (39, "Tumor on the optic nerve (optic glioma).These tumors usually appear by age 3, rarely in late childhood and adolescence, and almost never in adults.", 2957);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (40, "Learning disabilities.Impaired thinking skills are common in children with NF1, but are usually mild. Often there is a specific learning disability, such as problem with reading or mathematics. Attention-deficit/hyperactivity disorder (ADHD) is also common.", 2957);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (41, "Larger than average head size.Children with NF1 tend to have a larger than average head size due to increased brain volume.", 2957);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (42, "Short stature.Children with NF1 often are below average in height.", 2957);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (931, "Bipolar I disorder.You've had at least one manic episode that may be preceded or followed by hypomanic or major depressive episodes. In some cases, mania may trigger a break from reality (psychosis).", 1214);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (932, "Bipolar II disorder.You've had at least one major depressive episode and at least one hypomanic episode, but you've never had a manic episode.", 1214);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (933, "Cyclothymic disorder.You've had at least two years &mdash; or one year in children and teenagers &mdash; of many periods of hypomania symptoms and periods of depressive symptoms (though less severe than major depression).", 1214);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (934, "Other types.These include, for example, bipolar and related disorders induced by certain drugs or alcohol or due to a medical condition, such as Cushing's disease, multiple sclerosis or stroke.", 1214);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (450, "Sensation starts after being at rest.The sensation typically begins after you've been lying down or sitting for an extended time, such as in a car, airplane or movie theater.", 1364);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (451, "Relief by movement.The sensation of RLS/WED lessens with movement, such as stretching, jiggling your legs, pacing or walking.", 1364);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (452, "Worsening of symptoms in the evening.Symptoms occur mainly at night.", 1364);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (453, "Nighttime leg twitching.RLS/WED may be associated with another, more common condition called periodic limb movement of sleep, which causes your legs to twitch and kick, possibly throughout the night, while you sleep.", 1364);
