INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (694, "Age.Dupuytren's contracture occurs most commonly after the age of 50.", 1083);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (695, "Sex.Men are more likely to develop Dupuytren's and to have more severe contractures than are women.", 1083);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (696, "Ancestry.People of Northern European descent are at higher risk of the disease.", 1083);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (697, "Family history.Dupuytren's contracture often runs in families.", 1083);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (698, "Tobacco and alcohol use.Smoking is associated with an increased risk of Dupuytren's contracture, perhaps because of microscopic changes within blood vessels caused by smoking. Alcohol intake also is associated with Dupuytren's.", 1083);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (699, "Diabetes.People with diabetes are reported to have an increased risk of Dupuytren's contracture.", 1083);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (2110, "Blackheads.Small pitted areas of skin containing blackheads &mdash; often appearing in pairs or a 'double-barreled' pattern &mdash; are a common feature.", 1881);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (2111, "Red, tender bumps.These bumps often enlarge, break open and drain pus. The drainage may have an odor. Itching and burning may accompany the bumps. They usually appear in areas where skin rubs against skin.", 1881);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (2112, "Painful, pea-sized lumps.These hard lumps, which develop under the skin, may persist for years, enlarge and become inflamed.", 1881);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (2113, "Tunnels.Over time, tracts connecting the lumps may form under the skin. These wounds heal very slowly, if at all, and can leak pus.", 1881);
