INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (739, "Visual hallucinations.Hallucinations may be one of the first symptoms, and they often recur. They may include seeing shapes, animals or people that aren't there. Sound (auditory), smell (olfactory) or touch (tactile) hallucinations are possible.", 1743);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (740, "Movement disorders.Signs of Parkinson's disease (parkinsonian symptoms), such as slowed movement, rigid muscles, tremor or a shuffling walk may occur.", 1743);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (741, "Poor regulation of body functions (autonomic nervous system).Blood pressure, pulse, sweating and the digestive process are regulated by a part of the nervous system that is often affected by Lewy body dementia. This can result in dizziness, falls and bowel issues such as constipation.", 1743);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (742, "Cognitive problems.You may experience thinking (cognitive) problems similar to those of Alzheimer's disease, such as confusion, poor attention, visual-spatial problems and memory loss.", 1743);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (743, "Sleep difficulties.You may have rapid eye movement (REM) sleep behavior disorder, which can cause you to physically act out your dreams while you're asleep.", 1743);
INSERT INTO Symptoms (symptom_id, description, illness_id) VALUES (744, "Fluctuating attention.Episodes of drowsiness, long periods of staring into space, long naps during the day or disorganized speech are possible.", 1743);
